<style lang="scss">
.background-map-line-drawing-map {
  overflow: hidden;

  #background-map {
    height: auto;
    width: 140%;
    left: -20%;
    margin-top: -10%;
    padding: 10px 0;

    svg {
      z-index: -100;
    }

    justify-content: center;
  }
}

.square-abs {
  background-color: red;
  height: 10px;
  width: 10px;
  position: absolute;
  left: -5px;
  top: 5px;
  z-index: -90;
}
</style>

<script lang="ts">
import anime from "animejs/lib/anime.es.js";

const LOOP = {
  duration: 15000,
};

export default {
  mounted() {
    // Animate map loading
    anime({
      targets: ".background-map-line-drawing-map .background-map-lines path",
      strokeDashoffset: [anime.setDashoffset, 0],
      easing: "easeInSine",
      duration: 5000,
      delay: function (el, i) {
        return i * 100;
      },
    });

    // Animate cars
    this.animeVehicle("#vehicle-01", "#path-1", "linear", 10000, 800, 4200);
    this.animeVehicle("#vehicle-02", "#path-1", "linear", 10000, 2300, 2700);
    this.animeVehicle("#vehicle-03", "#path-2", "linear", 12000, 800, 2200);
    this.animeVehicle("#vehicle-04", "#path-3", "linear", 13500, 1000, 500);
    this.animeVehicle("#vehicle-05", "#path-3", "linear", 13750, 5000, 0);
    this.animeVehicle("#vehicle-06", "#path-4", "linear", 5000, 5000, 0);
    this.animeVehicle("#vehicle-07", "#path-5", "linear", 7000, 5000, 0);
    this.animeVehicle("#vehicle-08", "#path-6", "linear", 20000, 200, 0);
    this.animeVehicle("#vehicle-09", "#path-7", "linear", 35000, 200, 0);
  },
  methods: {
    animeVehicle(
      vehicle: string,
      path: string,
      easing: any = "linear",
      duration: number = LOOP.duration,
      delay: number = 0,
      endDelay: number = 0
    ) {
      if (duration + delay + endDelay != LOOP.duration) {
        this.$log.info(
          "BackgroundMap will run out of sync!",
          vehicle,
          path,
          duration + delay + endDelay
        );
      }
      var path = anime.path(path);
      anime({
        targets: vehicle,
        translateX: path("x"),
        translateY: path("y"),
        rotate: path("angle"),
        easing: easing,
        duration: duration,
        delay: delay,
        endDelay: endDelay,
        loop: true,
      });
    },
  },
};
</script>
